import _ from 'lodash';
export default {
  data: function data() {
    return {
      search: '',
      items: [{
        title: 'Bact-Nr'
      }, {
        title: 'Priority'
      }, {
        title: 'Einsender'
      }, {
        title: 'Pathogen'
      }],
      patientList: [],
      currentDataset2: {
        bactNr: '',
        wiederholung: '',
        infOldList: '',
        altId: '',
        priority: '',
        abbreviation: '',
        lastName: '',
        firstName: '',
        birthdate: '',
        entry: '',
        abnahmeDatum: '',
        einsender: '',
        station: '',
        bearbeitung: '',
        material: '',
        ngsProject: '',
        datumPrep: '',
        konzentration: '',
        visumDna: '',
        runNr: '',
        runProbeNr: '',
        libraryTyp: '',
        libraryDatum: '',
        libraryVisum: '',
        seqDatum: '',
        modalität: '',
        datenqualVisum: '',
        publicIdentifier: '',
        process: ''
      }
    };
  },
  computed: {
    //This Method filters the PatientList and builds the V-List that is displayed. 
    filteredItems: function filteredItems() {
      var _this = this;

      //   this.patientList = this.$store.state.NgsList
      return _.orderBy(this.patientList.filter(function (patient) {
        //the if just checks if the search is null, which happens when you clear the searchfield with the clearSearch() method
        if (!_this.search) return _this.items; //that block checks if any search input matches the data in the patientList. It loops trought the Patient and checks if a match results in true.
        //ex. patient.bactNr is the same as this.search then it returns the patients

        for (var item in patient) {
          return patient[item].toLowerCase().includes(_this.search.toLowerCase());
        }
      }));
    }
  },
  methods: {
    changeworkflow: function changeworkflow() {
      this.$router.push('/workflow');
    },
    created: function created() {
      for (var i = 0; i < this.$store.state.NgsList.length; i++) {
        if (this.$store.state.NgsList[i].process == 2) {
          this.currentDataset2 = this.$store.state.NgsList[i];
          this.patientList.push(this.currentDataset2);
        }
      }
    },
    //clears the search and sets the value null
    clearSearch: function clearSearch() {
      this.search = '';
    },
    //sets the currentPatient
    setCurrentData: function setCurrentData(patient) {
      this.$store.state.currentDataset2 = patient;
    }
  }
};
