import axios from 'axios';
export default {
  created: function created() {
    var _this = this;

    bus.$on('editedIndex', function (data) {
      _this.editedIndex = data;
    });
    bus.$on('editedPatient', function (data) {
      _this.editedPatient = data;
    });
    bus.$on('openFormular', function (data) {
      _this.dialog = data;
    });
  },
  data: function data() {
    return {
      patId: '',
      editedIndex: -1,
      dialog: false,
      editedPatient: {
        bactNr: '',
        wiederholung: '',
        infOldList: '',
        altId: '',
        priority: '',
        pathogen: '',
        lastName: '',
        firstName: '',
        birthdate: '',
        entry: '',
        abnahme: '',
