        ngsNr: '',
        libType: '',
        libDate: '',
        libVisum: '',
        seqDate: '',
        ngsMachine: '',
        qualityVisum: '',
        pubID: ''
      }
    };
  },
  methods: {
    close: function close() {
      var _this2 = this;

      this.dialog = false;
      setTimeout(function () {
        _this2.editedPatient = Object.assign({}, _this2.Patient);
        _this2.editedIndex = -1;
      }, 300);
    },
    onSubmit: function onSubmit() {
      var _this3 = this;

      var newPatient = {
        firstName: this.editedPatient.firstName,
        lastName: this.editedPatient.lastName,
        birthdate: this.editedPatient.birthdate
      };
      axios.post('http://147.87.118.201:3000/api/TblPatients', newPatient).then(function (ack1) {
        axios.get('http://147.87.118.201:3000/api/TblPatients/findOne?filter={"where":{"and":[{"firstName":"' + _this3.editedPatient.firstName + '"},{"lastName":"' + _this3.editedPatient.lastName + '"}]}}').then(function (res1) {
          console.log(res1);
          _this3.patId = res1.data.patientId;
          var newProbe = {
            patientId: _this3.patId,
            einsender: _this3.editedPatient.sender,
            station: _this3.editedPatient.station,
            eingangDatum: _this3.editedPatient.entry,
            material: _this3.editedPatient.material
          };
          axios.post('http://147.87.118.201:3000/api/TblProbeEingangs', newProbe).then(function (ack2) {
            axios.get('http://147.87.118.201:3000/api/TblProbeEingangs/findOne?filter={"where":{"patientId":"' + _this3.patId + '"}}').then(function (res2) {
              console.log(res2);
              var newNgs = {
                probeId: res2.data.probeId,
                bactNr: _this3.editedPatient.bactNr,
                wiederholung: _this3.editedPatient.wiederholung,
                pathogenId: _this3.editedPatient.pathogen,
                publicIdentifier: _this3.editedPatient.pubID,
                priority: _this3.editedPatient.priority
              };
              axios.post('http://147.87.118.201:3000/api/TblNgs', newNgs).then(function (ack3) {
                console.log(ack3);
              });
            });
          });
        });
      }).catch(function (error) {
        return console.log(error);
      });
      this.close();
      this.save();
    },
    // else statement = wen neuer Pat, if = editedPat. hier muss index und Pat übergeben werden.
    save: function save() {
      if (this.editedIndex > -1) {
        bus.$emit('patientHasChanged', this.editedPatient); // Object.assign(this.patients[this.editedIndex], this.editedPatient)
      } else {
        bus.$emit('newPatientCreated', this.editedPatient); // this.patients.push(this.editedPatient)
      }

      this.close();
    }
  }
};
